import React, { useState, useEffect } from 'react';
import { Cpu, Memory, HardDrive, Activity, Moon, Sun, Wifi, WifiOff } from 'lucide-react';
import Dashboard from './components/Dashboard';
import { ServerData } from './types';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [serverData, setServerData] = useState<ServerData | null>(null);

  useEffect(() => {
    // Check for saved dark mode preference
    const savedDarkMode = localStorage.getItem('darkMode') === 'true';
    setIsDarkMode(savedDarkMode);

    // Apply dark mode class
    if (savedDarkMode) {
      document.documentElement.classList.add('dark');
    }
  }, []);

  const toggleDarkMode = () => {
    const newDarkMode = !isDarkMode;
    setIsDarkMode(newDarkMode);
    localStorage.setItem('darkMode', newDarkMode.toString());

    if (newDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  };

  // Simulate real-time data updates
  useEffect(() => {
    const interval = setInterval(() => {
      // Simulate connection status
      setIsConnected(Math.random() > 0.1);

      // Simulate server data
      if (Math.random() > 0.1) {
        setServerData({
          cpu: {
            load: Math.random() * 100,
            cores: 8,
            temperature: 45 + Math.random() * 20
          },
          memory: {
            total: 16384,
            used: Math.random() * 16384,
            available: Math.random() * 8192
          },
          disk: {
            total: 1000000,
            used: Math.random() * 800000,
            available: Math.random() * 200000
          },
          processes: [
            { name: 'node', cpu: Math.random() * 20, memory: Math.random() * 1000 },
            { name: 'nginx', cpu: Math.random() * 10, memory: Math.random() * 500 },
            { name: 'mysql', cpu: Math.random() * 15, memory: Math.random() * 800 },
            { name: 'redis', cpu: Math.random() * 5, memory: Math.random() * 300 }
          ],
          uptime: Math.floor(Math.random() * 86400) + 3600,
          os: 'macOS 14.0',
          timestamp: new Date()
        });
      }
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-dark-900 transition-colors duration-200">
      {/* Header */}
      <header className="bg-white dark:bg-dark-800 shadow-sm border-b border-gray-200 dark:border-dark-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <Activity className="h-8 w-8 text-primary-600" />
                <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                  Server Monitor
                </h1>
              </div>
              <div className="flex items-center space-x-2">
                <div className={`status-indicator ${isConnected ? 'status-online' : 'status-offline'}`} />
                <span className="text-sm text-gray-600 dark:text-gray-400">
                  {isConnected ? 'Connected' : 'Disconnected'}
                </span>
                {isConnected ? <Wifi className="h-4 w-4 text-green-500" /> : <WifiOff className="h-4 w-4 text-red-500" />}
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <button
                onClick={toggleDarkMode}
                className="p-2 rounded-lg bg-gray-100 dark:bg-dark-700 hover:bg-gray-200 dark:hover:bg-dark-600 transition-colors"
                aria-label="Toggle dark mode"
              >
                {isDarkMode ? <Sun className="h-5 w-5" /> : <Moon className="h-5 w-5" />}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <Dashboard serverData={serverData} isConnected={isConnected} />
      </main>
    </div>
  );
}

export default App;
