import React from 'react';

interface DecorativeElementsProps {
  type?: 'floating' | 'corner' | 'loading' | 'status';
  size?: 'sm' | 'md' | 'lg';
  className?: string;
}

const DecorativeElements: React.FC<DecorativeElementsProps> = ({ 
  type = 'floating', 
  size = 'md', 
  className = '' 
}) => {
  const getSizeClass = () => {
    switch (size) {
      case 'sm': return 'w-4 h-4';
      case 'md': return 'w-6 h-6';
      case 'lg': return 'w-8 h-8';
      default: return 'w-6 h-6';
    }
  };

  const getAnimationClass = () => {
    switch (type) {
      case 'floating': return 'animate-float';
      case 'corner': return 'animate-bounce-slow';
      case 'loading': return 'animate-pulse-slow';
      case 'status': return 'animate-pulse-slow';
      default: return 'animate-float';
    }
  };

  return (
    <div className={`${getSizeClass()} bg-gradient-to-r from-purple-400 to-pink-400 rounded-full ${getAnimationClass()} ${className}`}>
    </div>
  );
};

// Loading spinner
export const LoadingSpinner: React.FC = () => (
  <div className="flex items-center justify-center">
    <div className="relative">
      <div className="animate-spin rounded-full h-16 w-16 border-4 border-purple-200 border-t-purple-600"></div>
      <div className="absolute inset-0 flex items-center justify-center">
        <div className="w-4 h-4 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full"></div>
      </div>
    </div>
  </div>
);

// Status indicator
export const StatusIndicator: React.FC<{ status: 'online' | 'offline' | 'warning' }> = ({ status }) => {
  const getStatusColor = () => {
    switch (status) {
      case 'online': return 'text-green-500';
      case 'offline': return 'text-red-500';
      case 'warning': return 'text-yellow-500';
      default: return 'text-gray-500';
    }
  };

  return (
    <div className={`w-3 h-3 rounded-full ${getStatusColor()} animate-pulse-slow`}>
    </div>
  );
};

// Decorative background
export const DecorativeBackground: React.FC = () => (
  <div className="absolute inset-0 overflow-hidden pointer-events-none">
    <div className="absolute top-10 left-10 w-20 h-20 bg-gradient-to-r from-pink-200 to-purple-200 dark:from-pink-800 dark:to-purple-800 rounded-full opacity-20 animate-bounce-slow"></div>
    <div className="absolute top-32 right-20 w-16 h-16 bg-gradient-to-r from-blue-200 to-cyan-200 dark:from-blue-800 dark:to-cyan-800 rounded-full opacity-30 animate-pulse-slow"></div>
    <div className="absolute bottom-20 left-1/4 w-12 h-12 bg-gradient-to-r from-yellow-200 to-orange-200 dark:from-yellow-800 dark:to-orange-800 rounded-full opacity-25 animate-bounce-slow" style={{ animationDelay: '1s' }}></div>
    <div className="absolute bottom-40 right-1/3 w-24 h-24 bg-gradient-to-r from-green-200 to-teal-200 dark:from-green-800 dark:to-teal-800 rounded-full opacity-15 animate-pulse-slow" style={{ animationDelay: '2s' }}></div>

    {/* Floating decorative elements */}
    <div className="absolute top-1/4 left-4 w-6 h-6 bg-gradient-to-r from-purple-400 to-pink-400 rounded-full animate-float opacity-60" style={{ animationDelay: '0.5s' }}></div>
    <div className="absolute top-1/2 right-8 w-4 h-4 bg-gradient-to-r from-pink-400 to-purple-400 rounded-full animate-float opacity-60" style={{ animationDelay: '1.5s' }}></div>
    <div className="absolute bottom-1/3 left-1/2 w-5 h-5 bg-gradient-to-r from-blue-400 to-cyan-400 rounded-full animate-float opacity-60" style={{ animationDelay: '2.5s' }}></div>
  </div>
);

// Footer decoration
export const FooterDecoration: React.FC = () => (
  <div className="text-center py-8">
    <div className="inline-flex items-center space-x-2 bg-gradient-to-r from-pink-100 to-purple-100 dark:from-pink-900/20 dark:to-purple-900/20 px-6 py-3 rounded-full shadow-lg">
      <div className="w-4 h-4 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full animate-bounce-slow"></div>
      <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
        Real-time monitoring active
      </span>
      <div className="w-4 h-4 bg-gradient-to-r from-pink-500 to-purple-500 rounded-full animate-pulse-slow"></div>
    </div>
  </div>
);

export default DecorativeElements;
