import React, { useState, useEffect } from 'react';
import { Activity, Moon, Sun, Wifi, WifiOff, Heart, Star } from 'lucide-react';
import Dashboard from './components/Dashboard';
import { ServerData } from './types';

function App() {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [serverData, setServerData] = useState<ServerData | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    // Check for saved dark mode preference
    const savedDarkMode = localStorage.getItem('darkMode') === 'true';
    setIsDarkMode(savedDarkMode);

    // Apply dark mode class
    if (savedDarkMode) {
      document.documentElement.classList.add('dark');
    }
  }, []);

  const toggleDarkMode = () => {
    const newDarkMode = !isDarkMode;
    setIsDarkMode(newDarkMode);
    localStorage.setItem('darkMode', newDarkMode.toString());

    if (newDarkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  };

  // Fetch real monitoring data
  const fetchMonitoringData = async () => {
    try {
      const response = await fetch('/api/monitoring');
      if (!response.ok) {
        throw new Error('Failed to fetch monitoring data');
      }
      const data = await response.json();
      setServerData(data);
      setIsConnected(true);
      setError(null);
    } catch (err) {
      console.error('Error fetching monitoring data:', err);
      setError(err instanceof Error ? err.message : 'Unknown error');
      setIsConnected(false);
    } finally {
      setIsLoading(false);
    }
  };

  // Initial data fetch
  useEffect(() => {
    fetchMonitoringData();
  }, []);

  // Set up polling for real-time updates
  useEffect(() => {
    const interval = setInterval(() => {
      fetchMonitoringData();
    }, 5000); // Update every 5 seconds

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-pink-50 via-purple-50 to-blue-50 dark:from-gray-900 dark:via-purple-900 dark:to-indigo-900 transition-all duration-500 relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute top-10 left-10 w-20 h-20 bg-gradient-to-r from-pink-200 to-purple-200 dark:from-pink-800 dark:to-purple-800 rounded-full opacity-20 animate-bounce-slow"></div>
        <div className="absolute top-32 right-20 w-16 h-16 bg-gradient-to-r from-blue-200 to-cyan-200 dark:from-blue-800 dark:to-cyan-800 rounded-full opacity-30 animate-pulse-slow"></div>
        <div className="absolute bottom-20 left-1/4 w-12 h-12 bg-gradient-to-r from-yellow-200 to-orange-200 dark:from-yellow-800 dark:to-orange-800 rounded-full opacity-25 animate-bounce-slow" style={{ animationDelay: '1s' }}></div>
        <div className="absolute bottom-40 right-1/3 w-24 h-24 bg-gradient-to-r from-green-200 to-teal-200 dark:from-green-800 dark:to-teal-800 rounded-full opacity-15 animate-pulse-slow" style={{ animationDelay: '2s' }}></div>
      </div>

      {/* Header */}
      <header className="bg-white/80 dark:bg-dark-800/80 backdrop-blur-md shadow-lg border-b border-gray-200/50 dark:border-dark-700/50 relative z-10">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-20">
            <div className="flex items-center space-x-6">
              <div className="flex items-center space-x-3">
                <div className="relative">
                  <Activity className="h-10 w-10 text-primary-600 animate-pulse" />
                  <div className="absolute -top-1 -right-1 w-4 h-4 bg-gradient-to-r from-pink-400 to-purple-400 rounded-full animate-ping"></div>
                </div>
                <div>
                  <h1 className="text-2xl font-bold bg-gradient-to-r from-purple-600 to-pink-600 bg-clip-text text-transparent">
                    Purr-fect Server Monitor
                  </h1>
                  <p className="text-xs text-gray-500 dark:text-gray-400 -mt-1">üê± Monitoring with style</p>
                </div>
              </div>
              <div className="flex items-center space-x-3">
                <div className={`status-indicator ${isConnected ? 'status-online' : 'status-offline'}`} />
                <span className="text-sm font-medium text-gray-700 dark:text-gray-300">
                  {isConnected ? 'Connected' : 'Disconnected'}
                </span>
                {isConnected ? 
                  <Wifi className="h-5 w-5 text-green-500 animate-pulse" /> : 
                  <WifiOff className="h-5 w-5 text-red-500" />
                }
              </div>
            </div>

            <div className="flex items-center space-x-4">
              {/* Cat-themed decorative elements */}
              <div className="hidden md:flex items-center space-x-2 text-pink-500 dark:text-pink-400">
                <Heart className="h-5 w-5 animate-pulse" />
                <span className="text-sm font-medium">Made with</span>
                <PawPrint className="h-5 w-5 animate-bounce" />
              </div>
              <button
                onClick={toggleDarkMode}
                className="p-3 rounded-xl bg-gradient-to-r from-gray-100 to-gray-200 dark:from-dark-700 dark:to-dark-600 hover:from-gray-200 hover:to-gray-300 dark:hover:from-dark-600 dark:hover:to-dark-500 transition-all duration-300 shadow-md hover:shadow-lg transform hover:scale-105"
                aria-label="Toggle dark mode"
              >
                {isDarkMode ? <Sun className="h-6 w-6 text-yellow-500" /> : <Moon className="h-6 w-6 text-purple-500" />}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 relative z-10">
        {error && (
          <div className="mb-6 p-6 bg-gradient-to-r from-red-50 to-pink-50 dark:from-red-900/20 dark:to-pink-900/20 border border-red-200 dark:border-red-800 rounded-2xl shadow-lg backdrop-blur-sm">
            <div className="flex items-center space-x-3">
              <div className="status-indicator status-offline" />
              <span className="text-red-800 dark:text-red-200 font-semibold">
                üòø Error: {error}
              </span>
            </div>
            <p className="text-red-600 dark:text-red-300 text-sm mt-2 ml-6">
              Make sure the backend server is running with: npm run server
            </p>
          </div>
        )}

        <Dashboard 
          serverData={serverData} 
          isConnected={isConnected} 
          isLoading={isLoading}
        />
      </main>

      {/* Floating Cat Elements */}
      <div className="fixed bottom-4 right-4 z-20 pointer-events-none">
        <div className="text-4xl animate-bounce-slow" style={{ animationDelay: '0.5s' }}>üê±</div>
      </div>
      <div className="fixed top-1/4 left-4 z-20 pointer-events-none">
        <div className="text-3xl animate-pulse-slow" style={{ animationDelay: '1.5s' }}>üò∫</div>
      </div>
    </div>
  );
}

export default App;
